// App.js
import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import Navbar from './Navbar';
import HomePage from './HomePage';
import CataloguePage from './CataloguePage';
import FavoritesPage from './FavoritesPage';

const App = () => {
  const [favoriteAlbums, setFavoriteAlbums] = useState([]);

  const handleAddToFavorites = (album) => {
    if (!favoriteAlbums.some((favorite) => favorite.id === album.id)) {
      setFavoriteAlbums((prevFavorites) => [...prevFavorites, album]);
      console.log('Album added to favorites:', album);
    } else {
      console.log('Album is already in favorites:', album);
    }
  };

  return (
    <Router>
      <Navbar />
      <Container>
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/cataloguepage">
            <CataloguePage handleAddToFavorites={handleAddToFavorites} />
          </Route>
          <Route path="/favoritespage">
            <FavoritesPage favoriteAlbums={favoriteAlbums} />
          </Route>
        </Switch>
      </Container>
    </Router>
  );
};

export default App;
