import React, { useState, useEffect } from 'react';
import { Card } from 'react-bootstrap';
import AddToFavoritesButton from './AddtoFavoritesButton';

const CataloguePage = () => {
  const [albums, setAlbums] = useState([]);
  const [favoriteAlbums, setFavoriteAlbums] = useState([]); // Add the favoriteAlbums state

  useEffect(() => {
    fetchAlbums()
      .then((data) => setAlbums(data))
      .catch((error) => console.log(error.message));
  }, []);

  const fetchAlbums = async () => {
    const response = await fetch('https://jsonplaceholder.typicode.com/albums');
    if (!response.ok) {
      throw new Error('Failed to fetch albums');
    }
    const data = await response.json();
    return data;
  };

  const handleAddToFavorites = (albumId) => {
    // Get the album from the albums array based on the albumId
    const albumToAdd = albums.find((album) => album.id === albumId);

    // Check if the album is already in the favorites array
    const isAlreadyInFavorites = favoriteAlbums.some((album) => album.id === albumId);

    if (albumToAdd && !isAlreadyInFavorites) {
      // Add the album to the favorites array
      setFavoriteAlbums((prevFavorites) => [...prevFavorites, albumToAdd]);
      console.log('Album added to favorites:', albumId);
    } else {
      console.log('Album is already in favorites or not found:', albumId);
    }
  };

  return (
    <div>
      <h1>Catalogue</h1>
      {albums.map(({ id, title }) => (
        <Card key={id}>
          <Card.Body>
            <Card.Title>{title}</Card.Title>
            <AddToFavoritesButton onClick={() => handleAddToFavorites(id)} />
          </Card.Body>
        </Card>
      ))}
    </div>
  );
};

export default CataloguePage;
