// App.jsimport React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import { Container } from 'react-bootstrap';
import Navbar from './Navbar';
import HomePage from './HomePage';
import CataloguePage from './CataloguePage';
import FavoritesPage from './FavoritesPage';

const App = () => {
  const [favoriteAlbums, setFavoriteAlbums] = useState([]);

  return (
    <Router>
      <Navbar />
      <Container>
        <Switch>
          <Route exact path="/" component={HomePage} />
          <Route path="/cataloguepage">
            <CataloguePage favoriteAlbums={favoriteAlbums} setFavoriteAlbums={setFavoriteAlbums} />
          </Route>
          <Route path="/favoritespage">
            <FavoritesPage favoriteAlbums={favoriteAlbums} />
          </Route>
        </Switch>
      </Container>
    </Router>
  );
};

export default App;
